import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

const Quote = () => {
    const [quote, setQuote] = useState(null);
    const history = useNavigate();
    const location = useLocation();
   const {token, chain} = location.state;

    useEffect(() => {
        const fetchQuote = async () => {
            try {
                const response = await axios.post('http://localhost:8080/quotes', { token, chain });
                setQuote(response.data);
            } catch (error) {
                console.error('Error fetching quote:', error);
            }
        };

        fetchQuote();
    }, [token, chain]);

    const handleBridge = () => {
        history.push('/params', { token, chain, quote });
    };

    return (
        <div>
            <h1>Quote</h1>
            {quote && (
                <div>
                    <p>Quote: {quote.amount}</p>
                    <button onClick={handleBridge}>Bridge</button>
                </div>
            )}
        </div>
    );
};

export default Quote;
