import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const [tokens, setTokens] = useState([]);
    const [chains, setChains] = useState([]);
    const [selectedToken, setSelectedToken] = useState('ZRO');
    const [selectedChain, setSelectedChain] = useState('chain1');
    const history = useNavigate();

    useEffect(() => {
        const fetchChains = async () => {
            try {
                const response = await axios.get('http://localhost:8080/tokens');
                setChains(response.data.supportedChains);
            } catch (error) {
                console.error('Error fetching tokens:', error);
            }
        };
        const fetchTokens = async () => {
            try {
                const response = await axios.get('http://localhost:8080/tokens', chains.chainId);
                setTokens(response.data.recommendedTokens);
            } catch (error) {
                console.error('Error fetching tokens:', error);
            }
        }

        fetchChains();
        fetchTokens();
    }, []);

    const data = {token: selectedToken, chain: selectedChain}
    const handleSubmit = async (e) => {
        e.preventDefault();
        history('/quote', { state: data });
        console.log(data);


    };

    return (
        <div>
            <h1>Select Token and Chain</h1>
            <form onSubmit={handleSubmit}>
                <select value={selectedToken} onChange={(e) => setSelectedToken(e.target.value)}>
                    {tokens.map((token) => (
                        <option key={token.id} value={token.id}>
                            {token.name}
                        </option>
                    ))} 
                </select>
                <select value={selectedChain} onChange={(e) => setSelectedChain(e.target.value)}>
                        {chains.map((token) => (
                        <option key={token.id} value={token.id}>
                            {token.name}
                        </option>
                    ))}
                </select>
                <button type="submit">Get Quote</button>
            </form>
        </div>
    );
};

export default Home;
