import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

const Quote = () => {
    const [quote, setQuote] = useState(null);
    const history = useNavigate();
    const location = useLocation();
    const data = location.state;
    const token = data.token;
    const chain = data.chain;
    const dsttoken = data.dsttoken;
    const dstchain = data.dstchain
    console.log(token);




    useEffect(() => {
        const fetchQuote = async () => {
            try {
                const response = await axios.get(`https://aggregator-api.xy.finance/v1/quote?srcChainId=${chain}&srcQuoteTokenAddress=${token}&srcQuoteTokenAmount=1000000000000000000&dstChainId=${dstchain}&dstQuoteTokenAddress=${dsttoken}&slippage=5`, {
                    headers: {},
                });
                setQuote(response.data.routes[0]);
                console.log(response.data.routes[0]);
            } catch (error) {
                console.error('Error fetching quote:', error);
            }
        };

        fetchQuote();
    }, [token, chain]);

    const handleBridge = () => {
        history.push('/params', { token, chain, quote });
    };

    return (
        <div >
            <h1 className='flex justify-center bg-slate-500'>Quote</h1>
            <div className='flex justify-center bg-slate-200'>
            {quote && (
                <div>
                    <p>Quote: ${(quote.srcQuoteTokenUsdValue)/1000000000000000000}</p>
                    <button className='flex justify-end' onClick={handleBridge}>Bridge</button>
                </div>
            )}
            </div>
        </div>
    );
};

export default Quote;
