import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

const Quote = () => {
    const [quote, setQuote] = useState(null);
    const history = useNavigate();
    const location = useLocation();
    const data = location.state;
    const token = data.token;
    const chain = data.chain;
    console.log(token);
    const price = (quote.srcQuoteTokenUsdValue)/1000000000000000000;



    useEffect(() => {
        const fetchQuote = async () => {
            try {
                const response = await axios.get(`https://aggregator-api.xy.finance/v1/quote?srcChainId=${chain}&srcQuoteTokenAddress=${token}&srcQuoteTokenAmount=1000000000000000000&dstChainId=10&dstQuoteTokenAddress=0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE&slippage=49`, {
                    headers: {},
                });
                setQuote(response.data.routes[0]);
                console.log(response.data.routes[0]);
            } catch (error) {
                console.error('Error fetching quote:', error);
            }
        };

        fetchQuote();
    }, [token, chain]);

    const handleBridge = () => {
        history.push('/params', { token, chain, quote });
    };

    return (
        <div>
            <h1>Quote</h1>
            {quote && (
                <div>
                    <p>Quote: ${price}</p>
                    <button onClick={handleBridge}>Bridge</button>
                </div>
            )}
        </div>
    );
};

export default Quote;
