import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Button } from "../components/ui/button"
import {
    Card,
    CardContent,
    CardDescription,
    CardFooter,
    CardHeader,
    CardTitle,
  } from "../components/ui/card"



const Home = () => {
    const [tokens, setTokens] = useState([]);
    const [chains, setChains] = useState([]);
    const [selectedToken, setSelectedToken] = useState('');
    const [tokenId, setTokenId] = useState(1);
    const [address, setAddress] = useState('');
    const [selectedChain, setSelectedChain] = useState('');
    const history = useNavigate();

    useEffect(() => {
        const fetchChains = async () => {
            try {
                const response = await axios.get('http://localhost:8080/tokens');
                setChains(response.data.supportedChains);
                fetchTokens(tokenId);
            } catch (error) {
                console.error('Error fetching chains:', error);
            }
        };



        fetchChains();
    }, []);

    const fetchTokens = async (tokenId) => {
        try {
            const response = await axios.get(`https://aggregator-api.xy.finance/v1/recommendedTokens?chainId=${tokenId}`);
            setTokens(response.data.recommendedTokens);
        } catch (error) {
            console.error('Error fetching tokens:', error);
        }
    }

    const onChangeValue = async (e) => {
       setTokenId(e.target.value);
       // console.log(e.target.value);
        fetchTokens(e.target.value);
    }
    const onChangeToken = async(e) => {
        setAddress(e.target.value);

    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = {token: address, chain: selectedChain}
        history('/quote', { state: data });
       // console.log(data);


    };

    return (
        <div className="container mx-auto px-4 py-8"> 
            <h1 className='text-2xl font-bold mb-4'>Select Token and Chain</h1>
            <form onSubmit={handleSubmit} className='bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4'>
                <select className='bg-slate-200' value={selectedToken} onChange={(e) => setSelectedToken(e.target.value)} onChangeCapture={onChangeToken}>
                    {tokens.map((token) => (
                        <option key={token.id} value={token.address}>
                            {token.name}


                        </option>
                    ))} 
                </select>
                <select value={selectedChain} onChange={(e) => setSelectedChain(e.target.value)} onChangeCapture={onChangeValue}>

                        {chains.map((token) => (

                        <option key={token.id} value={token.chainId}>

                            {token.name}

                        </option>
                    ))}
                </select>
                <Button className='bg-slate-200' type="submit" variant="outline">Get Quote</Button>

            </form>
        </div>
    );
};

export default Home;
