import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const [tokens, setTokens] = useState([]);
    const [selectedToken, setSelectedToken] = useState('ZRO');
    const [selectedChain, setSelectedChain] = useState('chain1');
    const history = useNavigate();

    useEffect(() => {
        const fetchTokens = async () => {
            try {
                const response = await axios.get('http://localhost:8080/tokens');
                setTokens(response.data.recommendedTokens);
            } catch (error) {
                console.error('Error fetching tokens:', error);
            }
        };

        fetchTokens();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        history('/quote', { token: selectedToken });
        history('/quote', { chain: selectedChain });
        console.log(selectedToken);
        console.log(selectedChain);

    };

    return (
        <div>
            <h1>Select Token and Chain</h1>
            <form onSubmit={handleSubmit}>
                <select defaultValue={selectedToken} onChange={(e) => setSelectedToken(e.target.value)}>
                    {tokens.map((token) => (
                        <option key={token.id} value={token.id}>
                            {token.name}
                        </option>
                    ))}
                </select>
                <select value={selectedChain} onChange={(e) => setSelectedChain(e.target.value)}>
                    {/* Add chain options here */}
                    <option value="chain1">Chain 1</option>
                    <option value="chain2">Chain 2</option>
                </select>
                <button type="submit">Get Quote</button>
            </form>
        </div>
    );
};

export default Home;
